    render() {
        if (!this.state.web3) {
            return <div>Loading Web3, accounts, and contracts...</div>;
        }
        return (
            <div className="App">
                <div className="container pt-3">
                    <StyledDropZone />   
                </div>
            </div>
        );
    }
}

export default App;
