import React, { Component } from "react";
import FileStorage from "./contracts/FileStorage.json";
import getweb3 from "./getWeb3";
import "./App.css";
import { StyledDropZone } from 'react-drop-zone';
import FileIcon, {defaultStyles} from 'react-file-icon';
import "react-drop-zone/dist/styles.css";
import "bootstrap/dist/css/bootstrap.css";
import { Table } from "reactstrap";



class App extends Component {
    state = {fileStorage: [], web3: null, accounts: null, contract: null};
    componentDidMount = async () => {
        try {
            // get network provider and web3 innstance.
            const web3 = await getweb3();

            // use web3 to get the user's account
            const accounts = await web3.eth.getAccounts();

            // get the cpntract instance
            const networkId = await web3.eth.net.getId();
            const deployedNetwork = FileStorage.networks[networkId];
            const instance = new web3.eth.Contract(
                FileStorage.abi,
                deployedNetwork && deployedNetwork.address,
            );

            //set web3, accounts and contract to the state, and then proceed with an
            // example of interacting with the contrac's method.
            this.setState({ web3, accounts, contract: instance}, this.runExample);
        } catch (error) {
            // catch any errors for any of the above operations.
            alert(
                `Failed to load web3, accounts, or contract. check console for details`,
            );
            console.log(error);
        }
    };

    getFiles = async () => {

    }

    onDrop = async () => {

    }

    render() {
        if (!this.state.web3) {
            return <div>Loading Web3, accounts, and contracts...</div>;
        }
        return (
            <div className="App">
                <div className="container pt-3">
                    <StyledDropZone />
                    <Table>
                        <thead>
                            <tr>
                                <th width="12%">Type</th>
                                <th className="text-center">File name</th>
                                <th className="text-right">Date</th>
                            </tr>
                            <thead>
                                <tr>
                                    <th><FileIcon size={30} extension="docx" {...defaultStyles.docx}/></th>
                                    <th className="text-center">file name.docx</th>
                                    <th className="text-right">2023/01/20</th>
                                </tr>
                            </thead>
                        </thead>
                    </Table> 
                </div>
            </div>
        );
    }
}

export default App;
